package com.hosu.accountbook.config;import io.swagger.v3.oas.models.Components;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.info.Info;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class SwaggerConfig {    @Bean    public OpenAPI openAPI(){        return new OpenAPI().components(new Components())                .info(apiInfo());    }    private Info apiInfo(){        return new Info()                .title("AccountBook API")                .description("가계부에서 사용할 API 명세서 정리")                .version("0.0.1");    }}