package com.hosu.accountbook.controller;import com.hosu.accountbook.common.response.ApiResponse;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.tags.Tag;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/users")@Tag(name = "유저 API")public class UserController {    @GetMapping    @Operation(summary = "연습용 예시입니다.", description = "hello를 반환하는 API")    public ApiResponse<String> returnHello() {        return ApiResponse.successResponse("hello");    }    @GetMapping("/error")    @Operation(summary = "연습용 예시입니다.", description = "error를 반환하는 API")    public ApiResponse<String> returnError(){        throw new RuntimeException("에러입니다.");    }}