package com.hosu.accountbook.domain;import com.hosu.accountbook.domain.enums.TransactionType;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Getter@Entitypublic class Category extends AuditingFields{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Setter @Column(nullable = false) private String categoryName;    @Setter @Column(nullable = false) private TransactionType categoryType;    @Setter    @JoinColumn(name = "userAccountId")    @ManyToOne(optional = false)    private UserAccount userAccount;}