package com.hosu.accountbook.domain;import com.hosu.accountbook.domain.enums.TransactionType;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import java.time.LocalDateTime;@Getter@Entitypublic class Transaction extends AuditingFields{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Setter    @Column(name = "transaction_type", nullable = false)    @Enumerated(value = EnumType.STRING)    private TransactionType transactionType;    @Setter @Column(nullable = false) private Long amount;    @Setter @Column(nullable = false) private LocalDateTime transactionDate;    @Setter private String description;    @Setter    @JoinColumn(name = "userAccountId")    @ManyToOne(optional = false)    private UserAccount userAccount;    @Setter    @JoinColumn(name = "categoryId")    @OneToOne(optional = false)    private Category category;}